INSERT INTO `ap_comment` VALUES (26,'20110373@student.hcmute.edu.vn','2023-05-19 16:16:39.905000','20110373@student.hcmute.edu.vn','2023-05-19 16:22:41.565000',1,'Các mẹ nhớ nên nên đặt trẻ nằm nghiêng, luân phiên nằm nghiêng bên phải rồi bên trái và ngược lại. Ở tư thế này trẻ sẽ đỡ bị sặc nếu bé trớ sữa ra. Dưới má trẻ có thể đặt một mảnh giấy hoặc vải mềm để lót.',24,68,_binary '',NULL),(27,'anhngocbui332@gmail.com','2023-05-19 16:22:41.561000','anhngocbui332@gmail.com','2023-05-19 16:22:41.561000',1,'Cảm ơn',6,68,_binary '\0',26),(28,'anhngocbui332@gmail.com','2023-05-19 16:25:13.306000','anhngocbui332@gmail.com','2023-05-19 16:25:13.306000',1,'Những dụng cụ ăn dặm cho bé mẹ nên chuẩn bị:\nThìa, muỗng\nKhay ăn có nhiều ngăn\nLy uống nước\nYếm ăn\nKhăn và giấy ăn\nGhế cho bé tập ăn dặm\nNồi/ cốc nấu cháo\nNồi hấp.',6,69,_binary '\0',NULL),(29,'thanhtoan08082002@gmail.com','2023-05-19 16:26:50.009000','thanhtoan08082002@gmail.com','2023-05-19 16:27:33.983000',1,'Các mẹ nên nhớ, những đứa trẻ sơ sinh không có nhiều cảm xúc, do đó nếu bé nhìn chằm chằm vào mắt, cười và bi bô như muốn nói chuyện với bạn thì các mẹ hãy yên tâm rằng mình chính là cả thế giới của bé!',20,70,_binary '',NULL),(30,'anhngocbui332@gmail.com','2023-05-19 16:27:33.981000','anhngocbui332@gmail.com','2023-05-19 16:29:05.413000',1,'Nhưng mà với ai nó cũng vậy',6,70,_binary '',29),(31,'thanhtoan08082002@gmail.com','2023-05-19 16:29:05.411000','thanhtoan08082002@gmail.com','2023-05-19 16:31:46.775000',1,'Vui lòng đọc lại vế 1',20,70,_binary '',30),(32,'anhngocbui332@gmail.com','2023-05-19 16:31:46.773000','anhngocbui332@gmail.com','2023-05-19 16:32:20.451000',1,'report',6,70,_binary '',31),(33,'thanhtoan08082002@gmail.com','2023-05-19 16:32:20.449000','thanhtoan08082002@gmail.com','2023-05-19 16:32:20.449000',1,'Thử đê',20,70,_binary '\0',32);
